$result = $conn->query("DROP VIEW `escandallos`;");
$result = $conn->query("create view escandallos AS select `p`.`id` AS `Id nombre plato`,`pe`.`id` AS `Id productos`,`c`.`descripcion` AS `Nombre partida`,`p`.`descripcion` AS `Nombre plato`,`pe`.`tipo_producto` AS `Origen`,`pe`.`descripcion` AS `Productos`,`pre`.`cantidad` AS `Peso racion`,`pe`.`coste` AS `Coste elaborado`,`pc`.`cantidad_base` AS `Cantidad final`,`pc`.`tiempo` AS `Tiempo` from ((((`productos_relacionados_elaborados` `pre` join `productos` `p` on((`pre`.`id_producto` = `p`.`id`))) join `productos` `pe` on((`pre`.`id_producto_relacionado` = `pe`.`id`))) join `categorias_elaborados` `c` on((`c`.`id` = `pre`.`id_categoria_estadisticas`))) join `productos_costes` `pc` on((`pc`.`id_producto` = `pe`.`id`))) order by `p`.`descripcion`;");